import React, { useEffect, useRef, useState } from 'react'
import './FeatureCourses.css';
import { Link, NavLink } from 'react-router-dom';
import { ServiceData } from '../Services/ServicesData';

const FeatureCourses = () => {
    const [windowWidth, setWindowWidth] = useState(window.innerWidth);
    const [scroll, setScroll] = useState();

    // const [k, setK] = useState(2);


    const [currentIndex, setCurrentIndex] = useState(0);
    const [featureCourse, setFeatureCourse] = useState([]);
    const containerRef = useRef(null);

    useEffect(() => {
    const featureCourses = ServiceData.filter((item) => typeof item.id === 'number')
    setFeatureCourse(featureCourses);
      // if (containerRef.current) {
      //   let rounded;
      //   let a = containerRef.current.offsetWidth;
      //   let b = a/25 + featureCourses.length;
      //   let c = b/16;        
      // }
      if (windowWidth <= 600) {
        setScroll(102);
      }
      else{
        setScroll(25)
      }
    }, [windowWidth]);
    // const decimalStr = width.toString().split('.')[1];
    // const i = decimalStr ? parseFloat(`0.${decimalStr}`) : 0;


      const nextSlide = () => {
            setCurrentIndex((prevIndex) =>
                prevIndex === featureCourse.length - 1 ? 0 : prevIndex +  1
            );        
      };

      const prevSlide = () => {

            setCurrentIndex((prevIndex) =>
                prevIndex === 0 ? featureCourse.length -1 : prevIndex -  1
              );
      };

  return (
    <>
    <section className="container"
            >
        <h2>Our Regular Services</h2>
        <p>We offer comprehensive services, from diagnostics to repairs, <br />ensuring your vehicle performs at its best with quality, efficiency, and care.</p>
        <div className="cards"
        >
        {/* <button className="slider-button prev" onClick={prevSlide}>
        &#10094;
      </button> */}
      <div className="slider_container"
            // ref={containerRef}
        >


            <div className="slider"
            style={{
              transform: `translateX(-${currentIndex * scroll}${scroll == '102' ? '%' : 'rem'})`,
              transition: 'transform 0.5s ease-in-out',
            }}
            >
        {
        featureCourse.map((card, index) => (
          <div
            key={index}
            className={`card`}
          >
            <div className="card_img">
              <img src={card.image} alt="" />
            </div>
            <p>{card.title}</p>
            <NavLink to={`/service/${card.id}`}>
            <button className='button3'>Read More</button>
            </NavLink>
          </div>
        ))}
        </div>
        </div>
        {/* <button className="slider-button next" onClick={nextSlide}>
        &#10095;
      </button> */}
</div>
    <hr className='hr'></hr>
    </section>
    </>
  )
}

export default FeatureCourses;
