import React from 'react'
import ShortBoxList from '../../Components/ShortBoxList/ShortBoxList'
import { CourseData } from './CoursesData'
import { Link } from 'react-router-dom'

const CoursesCategotries = () => {
  return (
    <>
    <ShortBoxList 
    heading={
        <>
        <h1>Discover Our Top Rated Courses</h1>
        </>
    }
    headpara={
        <>
        <p>Join our comprehensive Quranic education program designed to nurture both knowledge and spiritual growth at your own pace.</p>
        </>
    }
    >
            <div style={{
                display:'flex',
                flexWrap:'wrap',
                width:'100%',
                alignItems:'center',
                justifyContent:'center',
                gap:'1rem'
                }}>
            {CourseData.map((item, index) => (
                         <div
                         key={index}
                        //  className={`card`}
                         style={{
                            width:'25rem'
                         }}
                       >
                         <div className="card_img">
                           <img src={item.image} alt="" />
                         </div>
                         <p>{item.title}</p>
                         <Link className="button3" to={`/courses/${item.id}`}>Read More</Link>
                       </div>
            ))}
            </div>
    </ShortBoxList>
    </>
  )
}

export default CoursesCategotries
