import React, { useState } from "react";
import ShortBoxList from "../../Components/ShortBoxList/ShortBoxList";
import { CourseData } from "./CoursesData";
import { Link } from "react-router-dom";
import { CoursesCategoriesData } from "./CoursesCataegoriesData";

const CoursesCategotries = () => {
  const [selectedCourse, setSelectedCourse] = useState("Courses");

  const selectedCourseArray = CoursesCategoriesData.find(
    (item) => item.title === selectedCourse
  );
  return (
    <>
      <ShortBoxList
        heading={<>Discover Our Top Rated Courses</>}
        headpara={
          <>
            <p>
              Join our comprehensive Quranic education program designed to
              nurture both knowledge and spiritual growth at your own pace.
            </p>
          </>
        }
      >
        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            width: "100%",
            alignItems: "center",
            justifyContent: "center",
            gap: "1rem",
          }}
        >
          {selectedCourseArray.map((item, index) => (
            <div
              key={index}
              style={{
                width: "25rem",
                borderRadius: "2rem",
                display: "flex",
                flexDirection: "column",
                overflow: "hidden",
                backgroundColor: "var(--c1)",
                textAlign: "center",
                alignItems: "center",
                transition: ".3s",
                boxShadow: ".1rem .1rem 1rem gray",
                color: "var(--c3)",
                fontSize: "1.5rem",
              }}
            >
              <div className="card_img">
                <img src={item.image} alt="" />
              </div>
              <p>{item.title}</p>
              <Link
                className="button3"
                // to={`/courses/${item.id}`}
                onClick={() => setSelectedCourse(item.title)}
              >
                Read More
              </Link>
            </div>
          ))}
        </div>
      </ShortBoxList>
    </>
  );
};

export default CoursesCategotries;
