import React from 'react'
import ShortBoxList from '../../Components/ShortBoxList/ShortBoxList'
import { CourseData } from './CoursesData'

const CoursesCategotries = () => {
  return (
    <>
    <ShortBoxList 
    heading={
        <>
        <h1>Discover Our Top Rated Courses</h1>
        </>
    }
    headpara={
        <>
        <p>Join our comprehensive Quranic education program designed to nurture both knowledge and spiritual growth at your own pace.</p>
        </>
    }
    >
        <ul>
            {CourseData.map((item, index) => {
                <li key={index}>
                    {item.title}
                </li>
            })}
        </ul>
    </ShortBoxList>
    </>
  )
}

export default CoursesCategotries
