import React, { useEffect, useState } from 'react'
import ShortBoxList from '../../Components/ShortBoxList/ShortBoxList'
import { CourseData } from './CoursesData'
import { Link, useParams } from 'react-router-dom'

const CoursesCategotries = () => {
  const [paramsString, setParamsString] = useState('courses');


  const params = useParams();
  const [selectedCategory, setSelectedCategory] = useState([]);

  useEffect(() => {
    const product = CourseData.filter((item) => item.category == params.id);
    if (typeof product[0].id === 'number') {
      setParamsString('course')
    }
    setSelectedCategory(product);
  }, [params.id]);


  return (
    <>
    <ShortBoxList 
    heading={
        <>
        Discover Our Top Rated Courses
        </>
    }
    headpara={
        <>
        <p>Join our comprehensive education program designed to nurture both knowledge and spiritual growth at your own pace.</p>
        </>
    }
    >
            <div style={{
                display:'flex',
                flexWrap:'wrap',
                width:'100%',
                alignItems:'center',
                justifyContent:'center',
                gap:'1rem'
                }}>
            {selectedCategory.map((item, index) => (
                         <div
                         key={index}
                         style={{
                            width: '25rem',
                            borderRadius: '2rem',
                            display: 'flex',
                            flexDirection: 'column',
                            overflow: 'hidden',
                            backgroundColor: 'var(--c1)',
                            textAlign: 'center',                
                            alignItems: 'center',
                            transition: '.3s',
                            boxShadow: '.1rem .1rem 1rem gray',
                            color:'var(--c3)',
                            fontSize:'1.5rem'
                         }}
                       >
                         <div className="card_img">
                           <img src={item.image} alt="" />
                         </div>
                         <p>{item.title}</p>
                         <a className="button3" href={`/${paramsString}/${item.id}`}>Read More</a>
                       </div>
            ))}
            </div>
    </ShortBoxList>
    </>
  )
}

export default CoursesCategotries
